//import './App.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Task from './Task';

const API_URL = 'http://localhost:5001/api/tasks';
function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);
  const fetchTasks = async () => {
    try {
        const response = await axios.get(API_URL);
        console.log('Server response:', response);
        setTasks(response.data);
    } catch (error) {
        console.error('Error fetching tasks:', error);
    }
};
  const addTask = async () => {
    if (newTask.trim() !== '') {
      const response = await axios.post(API_URL, { name: newTask });
      setTasks([...tasks, response.data]);
      setNewTask('');
    }
  };
  //const deleteTask = async (taskId) => {

    //const response = await axios.delete(`${API_URL}/${taskId}`);
    //setTasks(tasks.filter(task => task.id !== response.data.id));
  //};
/*const deleteTask = async (taskId) => {
  try {
    const response = await axios.delete(`${API_URL}/${taskId}`);
    setTasks(tasks.filter(task => task.id !== response.data.id));
  } catch (error) {
    console.error('Error deleting task:', error);
  }
};*/
function deleteTask(taskId){
  const response = newTask;
  response.splice(taskId,1);
  setTask([... response]);
}

  return (
    <div>
      <h1>Task Tracker</h1>
      <div>
        <input
          type="text"
          placeholder="Add Task"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button onClick={addTask}>Add</button>
      </div>
      <ul>
      {tasks.map((task, taskId)=> {
        return < Task task= {task.id} deleteTask={deleteTask} taskId ={taskId}/>
      })}
      </ul>
    </div>
  );
}

export default App;
